import os
from dotenv import load_dotenv
import json
from web3 import Web3
import streamlit as st
#from utils.web3_utils import connect_to_infura
# Load the environment variables
load_dotenv()

# Connect to the Ethereum blockchain using Infura
infura_url = os.getenv("INFURA_KEY")
artist_wallet = os.getenv("ARTIST_WALLET")
# Connect to the Ethereum blockchain using Infura
w3 = Web3(Web3.HTTPProvider('http://127.0.0.1:7545'))

if w3.is_connected():
    st.write("Successfully connected to the Ethereum blockchain!")
else:
    st.write("Could not connect to the Ethereum blockchain!")

# Load the contract ABI
with open("./contracts/compiled/mv_contracts.json") as f:
    contract_abi = json.load(f)

# Declare the contract bytecode
contract_bytecode = ""

w3.eth.default_account = artist_wallet
Greeter = w3.eth.contract(abi=contract_abi, bytecode=contract_bytecode)

# Deploy Contract
tx_hash = Greeter.constructor().transact()
# Get Transaction Receipt

tx_receipt = w3.eth.waitForTransactionReceipt(tx_hash)
st.write(tx_receipt)
# Get Contract Address
contract_address = tx_receipt.contractAddress
st.session_state.contract_address = contract_address
st.write(contract_address)
